clear
opts = odeset('RelTol',2.2205e-14,'AbsTol',2.2205e-14);
[t,y] = ode113(@vdp1,[0 50],[pi/2; 0; pi/2; 0; pi/2; 0], opts);

l = 1;
x1 = (l/3)*sin(y(:,1));
y1 = (-l/3)*cos(y(:,1));
x2 = (l/3)*(sin(y(:,1)) + sin(y(:,3)));
y2 = (l/3)*(-cos(y(:,1)) - cos(y(:,3)));
x3 = (l/3)*(sin(y(:,1)) + sin(y(:,3)) + sin(y(:,5)));
y3 = (l/3)*(-cos(y(:,1)) - cos(y(:,3)) - cos(y(:,5)));

for k = 1:length(x1)
    clf
    h = animatedline('Marker', 'o');
    axis([-1.5,1.5,-1.5,1.5])
    axis square
    addpoints(h, 0, 0);
    addpoints(h,x1(k),y1(k));
    addpoints(h,x2(k),y2(k));
    addpoints(h,x3(k),y3(k));
    drawnow
end


